// Simple dialogues
import k from "./kaboom";
import {alien1} from "./items"
import Game from "./satellite";
// import totalScore from './items';

let correctQuiz = false;

function Quiz(){

  loadSprite("alien1", alien1);

  k.scene("quiz", () => {
    // Define the dialogue data
    const dialogs = [
      ["alien1", "This is quiz time!"],
      ["alien1", "Q. What is my name?"],
    ];

  let curDialog = 0;

  // Text bubble
  const textbox = add([
    rect(width() - 700, 120, { radius: 32 }),
    origin("center"),
    pos(center().x, height() - 400),
    outline(2),
  ]);

  // Text
  const txt = add([
    text("", { size: 32, width: width() - 230 }),
    pos(textbox.pos),
    origin("center"),
  ]);

  // Character avatar
  const avatar = add([
    sprite("alien1"),
    scale(0.5),
    origin("center"),
    rotate(0),
    pos(center().sub(0, 200))
  ]);

  avatar.onUpdate(() => {
    // .angle is a property provided by rotate() component, here we're incrementing the angle by 120 degrees per second, dt() is the time elapsed since last frame in seconds
    avatar.angle += 120 * dt()
  })
  

  onKeyPress("space", () => {
    // Cycle through the dialogs
    curDialog = (curDialog + 1) % dialogs.length;
    updateDialog();
  });

  // Update the on screen sprite & text
  function updateDialog() {
    const [char, dialog] = dialogs[curDialog];

    // Use a new sprite component to replace the old one
    avatar.use(sprite(char));
    // Update the dialog text
    txt.text = dialog;
  }

  updateDialog();

  const addButton = (txt, p, f) => {
    const btn = add([
      text(txt),
      pos(p),
      area({ cursor: "pointer" }),
      scale(1),
      origin("center"),
    ]);

      btn.onClick(f);

      btn.onUpdate(() => {
        if (btn.isHovering()) {
          const t = time() * 10;
          btn.color = rgb(
            wave(0, 255, t),
            wave(0, 255, t + 2),
            wave(0, 255, t + 4)
          );
          btn.scale = vec2(1.2);
        } else {
          btn.scale = vec2(1);
          btn.color = rgb();
        }
      });
    };
    
    //   scene("start", () => {
    //     addButton("Start", vec2(k.width() * 0.5, k.height() * 0.5), () => k.go('inputName'));
    //   })
    const answer = [["Alien"], ["Joo"]];
    addButton("1." + answer[0], vec2(width() * 0.4, height() * 0.8), () =>
      correct()
    );
    addButton("2." + answer[1], vec2(width() * 0.6, height() * 0.8), () =>
      wrong()
    );

    let score = 0;

    const correct = () => {
      // debug.log("correct in");
      correctQuiz = true;
      // debug.log("totalScore: " + totalScore);
      Game();

    };


    const wrong = () => {
      // debug.log("wrong in");
      // k.go("lose");
      correctQuiz = false;
      Game();
    }
  });
  //   const wrong = () => {
  //     debug.log("wrong in");
  //     k.go("lose");
  //   }
  // });

  k.scene("lose", () => {
    add([text("Game over"), pos(center()), origin("center")]);
  });

  
  k.go("quiz");
}

export { Quiz, correctQuiz } ;
